package Week4_IOStream_Threading.EX1;import java.io.*;class Data1 implements Runnable {    private File file;    public Data1(File file) {        this.file = file;    }    @Override    public void run() {        try {            FileReader fileReader = new FileReader(this.file.getPath());            BufferedReader bufferedReader = new BufferedReader(fileReader);            String line;            while((line = bufferedReader.readLine())!=null){                System.out.println(line);            }        } catch (FileNotFoundException e) {            throw new RuntimeException(e);        } catch (IOException e) {            throw new RuntimeException(e);        }    }}public class Run {    public static void main(String[] args) {        File file = new File("C:\\Users\\ADMIN\\Documents\\TepText\\Week4_IOStream\\Bai1.txt");        Data1 d = new Data1(file);        Thread thread = new Thread(d);        thread.start();    }}