package Week4_IOStream_Threading.EX2;import java.io.*;class Data {    private File file;    private String data;    public Data(File file, String data) {        this.file = file;        this.data = data;    }    public void writeDatainFile() throws IOException, InterruptedException {        FileWriter fileWriter = new FileWriter(this.file, true);        BufferedWriter bw = new BufferedWriter(fileWriter);        bw.write(data);        bw.newLine();        bw.close();        fileWriter.close();    }}class ReaderFile implements Runnable{    private Data data;    public ReaderFile(Data data){        this.data = data;    }    @Override    public void run() {        try {            this.data.writeDatainFile();        } catch (IOException e) {            throw new RuntimeException(e);        } catch (InterruptedException e) {            throw new RuntimeException(e);        }    }}public class Run {    public static void main(String[] args) {        Thread one = new Thread(new ReaderFile(new Data(new File("C:\\Users\\ADMIN\\Documents\\TepText\\Week4_IOStream\\Bai2.txt"), "con ca")));        Thread two = new Thread(new ReaderFile(new Data(new File("C:\\Users\\ADMIN\\Documents\\TepText\\Week4_IOStream\\Bai2.txt"), "con cat")));        one.start();        two.start();    }}