package Week4_IOStream_Threading.EX3;import java.io.*;class Data implements Runnable {    private File file1;    private File file2;    public Data(File file1, File file2){        this.file1 = file1;        this.file2 = file2;    }    @Override    public void run() {        FileReader fileRead = null;        try {            fileRead = new FileReader(this.file1.getPath());        } catch (FileNotFoundException e) {            throw new RuntimeException(e);        }        BufferedReader bufferedReader = new BufferedReader(fileRead);        try {            FileWriter fileWriter = new FileWriter(this.file2.getPath());            BufferedWriter bufferedWriter = new BufferedWriter(fileWriter);            String line;            while ((line = bufferedReader.readLine())!=null) {                bufferedWriter.write(line);                bufferedWriter.newLine();            }            bufferedWriter.flush();            bufferedReader.close();            bufferedWriter.close();        } catch (IOException e) {            throw new RuntimeException(e);        }    }}public class Run {    public static void main(String[] args) {        Thread thread = new Thread(new Data(new File("C:\\Users\\ADMIN\\Documents\\TepText\\Week4_IOStream\\Bai2.txt"), new File("C:\\Users\\ADMIN\\Documents\\TepText\\Week4_IOStream\\Bai3.txt")));        thread.start();    }}