package Week4_IOStream_Threading.EX7;import java.io.*;class Data implements Runnable {    File source;    public Data(File source) {        this.source = source;    }    @Override    public void run() {        source = new File(source.getPath());        InputStreamReader isr = new InputStreamReader(System.in);        BufferedReader br = new BufferedReader(isr);        FileWriter fw = null;        try {            fw = new FileWriter(source);        } catch (IOException e) {            throw new RuntimeException(e);        }        BufferedWriter bw = new BufferedWriter(fw);        String line;        while(true){            try {                if (!!(line = br.readLine()).equals("exit")) break;            } catch (IOException e) {                throw new RuntimeException(e);            }            try {                bw.write(line);                bw.newLine();            } catch (IOException e) {                throw new RuntimeException(e);            }        }        try {            bw.flush();            bw.close();            br.close();        } catch (IOException e) {            throw new RuntimeException(e);        }    }}public class Run {    public static void main(String[] args) {        Thread thread = new Thread(new Data(new File("C:\\Users\\ADMIN\\Documents\\TepText\\Week4_IOStream\\Bai7.txt")));        thread.start();    }}